import sys

PI = 3.141592

def warn(fmt, *args):
    print(fmt % args, file=sys.stderr)

def die(fmt, *args):
    warn(fmt, *args)
    exit()

def deg2rad(degree):
    return 2 * PI * degree / 360

def rad2deg(rad):
    return 360 * rad / (2 * 3.141592)

def cmp(a, b):
    if a > b:
        return 1
    elif a < b:
        return -1
    else:
        return 0
